package com.webreach.mirth.plugins.ihe;

import com.misyshealthcare.connect.util.LibraryConfig.ILogContext;

public class IheLogContext implements ILogContext{
	String userId;
	String userName;
	String userSystem;
	String clientAddress;
	
	public IheLogContext()
	{
		
	}
	
	public IheLogContext(String userId, String userName, String userSystem, String clientAddress)
	{
		this.userId = userId;
		this.userName = userName;
		this.userSystem = userSystem;
		this.clientAddress = clientAddress;
	}
	
	public String getClientAddress() {
		// TODO Auto-generated method stub
		return clientAddress;
	}

	public String getUserId() {
		// TODO Auto-generated method stub
		return userId;
	}

	public String getUserName() {
		// TODO Auto-generated method stub
		return userName;
	}

	public String getUserSystem() {
		// TODO Auto-generated method stub
		return userSystem;
	}

}
